const navSlide=()=>{const e=document.querySelector(".burger"),t=document.querySelector("#navigation-bar").offsetHeight,n=document.querySelector(".nav-links"),a=document.querySelectorAll(".nav-links a"),i=document.querySelector(".content");window.addEventListener("resize",()=>{window.innerWidth>576&&(n.classList.remove("nav-active"),e.classList.remove("toggle"),i.style.animation="",a.forEach(e=>{e.style.animation&&(e.style.animation="")}))}),e.addEventListener("click",()=>{n.classList.toggle("nav-active"),n.style.top=`${t}px`,a.forEach((e,t)=>{e.style.animation?(e.style.animation="",Object.is(i,null)||(i.style.animation="movePageContentLeft 0.5s ease forwards")):(e.style.animation=`navLinkFadeIn 0.3s ease forwards ${t/7+.5}s`,Object.is(i,null)||(i.style.animation="movePageContentRight 0.5s ease forwards"))}),e.classList.toggle("toggle")})};navSlide();